    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       double l=0;
       double m=0;
       double k=0;
       double h=0, cte=0, cts=0, ctse=0, cs=0;
       int n=0,n1=0, cb=0;
       
       
       l=Double.parseDouble(this.lambda.getText());
       m=Double.parseDouble(this.mu.getText());
       k=Double.parseDouble(this.k.getText());
       n=Integer.parseInt(this.n.getText());
       n1=Integer.parseInt(this.n1.getText());
       cb=this.Cb.getSelectedIndex();
       
       
       double cPo=CalcularPo(l,m,k);
       Po.setText(String.valueOf(cPo));
       
       double cPk=CalcularPk(l,m,k);
       Pk.setText(String.valueOf(cPk));
       
       double cPn=CalcularPn(l,m,k,n);
       Pn.setText(String.valueOf(cPn));
       
       double cPne=CalcularPne(l,m,k);
       Pne.setText(String.valueOf(cPne));
       
       double cL=CalcularL(l,m,k);
       L.setText(String.valueOf(cL));
       
       double cLq=CalcularLq(l,m,k);
       Lq.setText(String.valueOf(cLq));
       
       double cLn=CalcularLn(l,m,k);
       Ln.setText(String.valueOf(cLn));
       
       double cW=CalcularW(l,m,k);
       W.setText(String.valueOf(cW));
       
       double cWq=CalcularWq(l,m,k);
       Wq.setText(String.valueOf(cWq));
       
       double cWn=CalcularWn(l,m,k);
       Wn.setText(String.valueOf(cWn));
              
       double cP=Probabilidad(cb,l,m,k,n1);
       Num.setText(String.valueOf(cP));
       
       
    }//GEN-LAST:event_jButton1ActionPerformed

