    public static double Factorial(int a)
    {
        double b=1;
            for(int num=a;num>=1;num--)
            {
                b*=num;
            }
        return b;
    }
    
    public static double CalcularPo(double l, double m, double k)
    {
        double sumatoria=0;
            for(int ni=0;ni<=(k-1);ni++)
            {
                double factor=1/Factorial(ni);
                double elevado = pow((l/m),ni);

                double respuesta = factor*elevado;
                sumatoria=sumatoria+respuesta;
            }

        double segundaparte = 0;
            segundaparte = (1/Factorial((int)k))*(pow((l/m), (k))*((k*m)/((k*m)-l)));
        double respuestaPo=1/(sumatoria+segundaparte);//Respuesta Po
        
        return respuestaPo;
    }
    
    public static double CalcularPk(double l, double m, double k)
    {
        double parteUno=(1 / Factorial((int)k));
        double parteDos = pow((l/m), k);
        double parteTres = (k * m) / ((k * m) - l);
        double respuestaPk = parteUno * parteDos * parteTres * CalcularPo(l,m,k);//Respuesta Pk
        
        return respuestaPk;
    }
    
    public static double CalcularPne(double l, double m, double k)
    {
        double respuestaPne=1-CalcularPk(l,m,k); //Respuesta Pne
        
        return respuestaPne;
    }
    
    public static double CalcularPn(double l, double m, double k, int n)
    {
        double respuestaPn=0;
        if (n <= k)
        {
            respuestaPn = (CalcularPo(l,m,k)/ Factorial((int)n)) * pow(((l / m)), n);
        }
        else if (n > k)
        {
            respuestaPn = (1 / (Factorial((int)k) * pow(k, (n - k)))) * pow((l / m), n);
        }
        
        return respuestaPn;
    }
    
    public static double CalcularL(double l, double m, double k)
    {
        double arriba = (l * m) * pow((l/m),k);
        double abajo = Factorial((int)(k-1))*pow(((k*m)-l),2);
        double alado = l / m;

        double respuestaL = ((arriba/abajo) * CalcularPo(l,m,k)) + alado; //Respuesta L
        
        return respuestaL;
    }
    
    public static double CalcularLq(double l, double m, double k)
    {
        double arribaLq = (l * m) * pow((l / m), k)*CalcularPo(l,m,k);
        double abajoLq = Factorial((int)(k - 1)) * pow(((k * m) - l), 2);

        double respuestaLq = arribaLq / abajoLq; //Respuesta Lq
        
        return respuestaLq;
    }
    
    public static double CalcularLn(double l, double m, double k)
    {
        double respuestaLn = CalcularLq(l,m,k)/ CalcularPk(l,m,k); //Respuesta Ln
        
        return respuestaLn;
    }
    
    public static double CalcularW(double l, double m, double k)
    {
        double arribaW = m * pow((l / m), k) * CalcularPo(l,m,k);
        double abajoW = Factorial(((int)k - 1)) * pow(((k * m) - l), 2);
        double aladoW = 1 / m;

        double respuestaW = (arribaW/ abajoW)+aladoW;   //Respuesta W
        
        return respuestaW;
    }
    
    public static double CalcularWq(double l, double m, double k)
    {
        double arribaWq =m * pow((l / m), k) * CalcularPo(l,m,k);
        double abajoWq = Factorial((int)(k - 1)) * pow(((k * m) - l), 2);

        double respuestaWq = arribaWq/ abajoWq;     //Respuesta Wq
        
        return respuestaWq;
    }
    
    public static double CalcularWn(double l, double m, double k)
    {
        double respuestaWn = CalcularWq(l,m,k) / CalcularPk(l,m,k);   //Respuesta Wn
        
        return respuestaWn;
    }
    
    public static double CalcularCtte(double l, double m, double k, double h, double cte)
    {
        double respuestaCtte=(l*h*CalcularWq(l,m,k)*cte);
        respuestaCtte=Math.round(respuestaCtte*100);
        respuestaCtte=respuestaCtte/100;
        return respuestaCtte;
    }
    
    public static double CalcularCtts(double l, double m, double k, double h, double cts)
    {
        double respuestaCtts=(l*h*CalcularW(l,m,k)*cts);
        respuestaCtts=Math.round(respuestaCtts*100);
        respuestaCtts=respuestaCtts/100;
        return respuestaCtts;
    }
    
    public static double CalcularCttse(double l, double m, double k, double h, double ctse)
    {
        double respuestaCttse=(l*h*CalcularW(l,m,k)*ctse);
        respuestaCttse=Math.round(respuestaCttse*100);
        respuestaCttse=respuestaCttse/100;
        return respuestaCttse;
    }
    
    public static double CalcularCts(double k, double cs)
    {
        double respuestaCts=k*cs;
        return respuestaCts;
    }
    
    public static double CalcularCt(double l,double m,double k,double h,double cte,double cts,double ctse,double cs)
    {
        double respuestaCt=CalcularCtte(l,m,k,h,cte)+CalcularCtts(l,m,k,h,cts)+CalcularCttse(l,m,k,h,ctse)+CalcularCts(k,cs);
        respuestaCt=Math.round(respuestaCt*100);
        respuestaCt=respuestaCt/100;
        return respuestaCt;
    }
    
    public static double Probabilidad(int cb, double l, double m, double k, int n1)
    {
        double respuesta =0;
        
        if(cb==0)
        {
            for(int i=0;i<n1;i++){
                respuesta+=CalcularPn(l,m,k,i);
            }
            respuesta=1-respuesta;
        }
        else
        {
            for(int i=0;i<=n1;i++){
                respuesta+=CalcularPn(l,m,k,i);
            }
        } 
       
        return respuesta;
    }
